import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchPosts,
  addPost,
  editPost,
  removePost,
} from "./features/posts/postsSlice";

function App() {
  const dispatch = useDispatch();
  const { items: posts, loading } = useSelector((state) => state.posts);

  useEffect(() => {
    dispatch(fetchPosts());
  }, [dispatch]);

  return (
    <div>
      <h1>Posts Manager</h1>
      {loading && <p>Loading...</p>}
      {posts.map((post) => (
        <div key={post._id}>
          <h3>{post.title}</h3>
          <p>{post.body}</p>
          <button onClick={() => dispatch(removePost(post._id))}>Delete</button>
          <button
            onClick={() =>
              dispatch(
                editPost({
                  id: post._id,
                  data: { ...post, title: post.title + " (edited)" },
                })
              )
            }
          >
            Edit
          </button>
        </div>
      ))}
      <button
        onClick={() =>
          dispatch(
            addPost({ title: "New Redux Post", body: "Test", userId: 1 })
          )
        }
      >
        Add Post
      </button>
    </div>
  );
}

export default App;
