import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  getPosts,
  createPost,
  updatePost,
  deletePost,
} from "./features/posts/postsSlice";
import PostList from "./components/PostList";
import PostForm from "./components/PostForm";

function App() {
  const [editingPost, setEditingPost] = useState(null);
  const dispatch = useDispatch();
  const { items: posts, loading } = useSelector((state) => state.posts);

  useEffect(() => {
    dispatch(getPosts());
  }, [dispatch]);

  const handleCreateOrUpdate = async (post) => {
    if (editingPost) {
      await dispatch(updatePost({ id: editingPost._id, data: post }));
    } else {
      await dispatch(createPost(post));
    }
    setEditingPost(null);
  };

  const handleDelete = async (id) => {
    await dispatch(deletePost(id));
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div style={{ padding: "20px" }}>
      <h1>Posts Manager</h1>
      <PostForm
        onSubmit={handleCreateOrUpdate}
        editingPost={editingPost}
        onCancel={() => setEditingPost(null)}
      />
      <PostList posts={posts} onEdit={setEditingPost} onDelete={handleDelete} />
    </div>
  );
}

export default App;
