import React, { useState, useEffect } from "react";

function PostForm({ onSubmit, editingPost, onCancel }) {
  const [form, setForm] = useState({ title: "", body: "", userId: "" });

  useEffect(() => {
    if (editingPost) {
      setForm(editingPost);
    }
  }, [editingPost]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(form);
    setForm({ title: "", body: "", userId: "" });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>{editingPost ? "Edit Post" : "Create Post"}</h2>
      <input
        type="text"
        name="title"
        value={form.title}
        onChange={handleChange}
        placeholder="Title"
        required
      />
      <br />
      <textarea
        name="body"
        value={form.body}
        onChange={handleChange}
        placeholder="Body"
        required
      />
      <br />
      <input
        type="number"
        name="userId"
        value={form.userId}
        onChange={handleChange}
        placeholder="User ID"
        required
      />
      <br />
      <button type="submit">{editingPost ? "Update" : "Create"}</button>
      {editingPost && <button onClick={onCancel}>Cancel</button>}
    </form>
  );
}

export default PostForm;
