import api from "..api";

// get posts
export const getPosts = () => api.get("/posts");

// create
export const createPost = (post) => api.post("/posts", post);

// upd
export const updatePost = (id, post) => api.put(`/posts/${id}`, post);

// del
export const deletePost = (id) => api.delete(`/posts/${id}`);
