import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  getPosts,
  createPost,
  updatePost,
  deletePost,
} from "./features/posts/postsSlice";
import PostList from "./components/PostList";
import PostForm from "./components/PostForm";

function App() {
  const [editingPost, setEditingPost] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const dispatch = useDispatch();
  const { items: posts, loading } = useSelector((state) => state.posts);

  useEffect(() => {
    dispatch(getPosts());
  }, [dispatch]);

  const handleCreateOrUpdate = async (post) => {
    if (editingPost) {
      await dispatch(updatePost({ id: editingPost._id, data: post }));
    } else {
      await dispatch(createPost(post));
    }
    setEditingPost(null);
    setShowForm(false);
  };

  const handleDelete = async (id) => {
    await dispatch(deletePost(id));
  };

  const handleEdit = (post) => {
    setEditingPost(post);
    setShowForm(true);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div style={{ padding: "20px" }}>
      <h1>Posts Manager</h1>
      {!showForm && (
        <button
          onClick={() => {
            setShowForm(true);
            setEditingPost(null);
          }}
        >
          Create Post
        </button>
      )}
      <PostForm
        visible={showForm}
        onSubmit={handleCreateOrUpdate}
        editingPost={editingPost}
        onCancel={() => {
          setEditingPost(null);
          setShowForm(false);
        }}
      />
      <PostList posts={posts} onEdit={handleEdit} onDelete={handleDelete} />
    </div>
  );
}

export default App;
